import { useState } from 'react';
import { Layout, Menu } from 'antd';
import { Link, Routes, Route } from 'react-router-dom';
import routes from './routes';
import Home from './pages/Home';

const { Header, Content, Sider } = Layout;

function App() {

  const currentPath = window.location.pathname;

  const [selectedPath, setSelectedPath] = useState(
    routes.find(item => currentPath.includes(item.path))?.path || ''
  )



  return (
    <div className="App">
      <a href="/micro-react">micro-react</a> |
      <a href="/micro-vue2">micro-vue2</a> |
      <a href="/micro-vue3">micro-vue3</a>
      <div id="micro-react"></div>
      <div id="micro-vue2"></div>
      <div id="micro-vue3"></div>
    </div>
  );
}

export default App;
