import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from "react-router-dom";
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import './public-path';


let root: ReactDOM.Root;

function render(props) {
  const { container, routerBase } = props;
  root = ReactDOM.createRoot(container ? container.querySelector('#root') : document.getElementById('root') as HTMLElement);
  root.render(
    <React.StrictMode>
      <BrowserRouter
        basename={window.__POWERED_BY_QIANKUN__ ? routerBase : "/"}
      >
        <App />
      </BrowserRouter>
    </React.StrictMode>
  );
}

// @ts-ignore
export async function bootstrap() {
  console.log('eact app bootstraped');
}

// 应用每次进入都会调用mount方法，通常我们在这里触发应用的渲染方法
// @ts-ignore
export async function mount(props) {
  console.log('props from main framework', props);
  root.render(
    <React.StrictMode>
      <App />
    </React.StrictMode>
    , props.container ? props.container.querySelector("#root") : document.getElementById("root"));
}

export async function unmount(props) {
  const { container } = props;
  ReactDOM.unmountComponentAtNode(container ? container.querySelector('#root') : document.querySelector('#root'));
}

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
